import React, { useState } from 'react';
import { TrendingUp, TrendingDown, IndianRupee } from 'lucide-react';
import { Doughnut } from 'react-chartjs-2';
import SummaryCard from './SummaryCard';
import ProjectCard from './ProjectCard';

const CompanyDashboard = ({
  organization,
  selectedDept,
  onDeptChange,
  onProjectSelect,
  onAddProject,
  onFileUpload,
  onBack,
  projects = [],
  totals = { ar: 0, ap: 0 },
}) => {
  const [dashboardView, setDashboardView] = useState('all');

  // All transactions from all projects
  const allTxs = projects.flatMap((p) => p.transactions || []);

  // Organization-level transactions (no ProjectID)
  const orgTxs = allTxs.filter(tx => !tx.ProjectID && !tx.projectId);

  // Project-level transactions (with ProjectID)
  const projectTxs = allTxs.filter(tx => tx.ProjectID || tx.projectId);

  // Filter AR/AP
  const isAR = tx => (tx.ExpenseType || tx.expenseType)?.toLowerCase() === 'income' || (tx.ExpenseType || tx.expenseType)?.toLowerCase() === 'receipt';
  const isAP = tx => (tx.ExpenseType || tx.expenseType)?.toLowerCase() === 'expense';

  const orgFilteredTxs = dashboardView === 'all'
    ? orgTxs
    : orgTxs.filter(tx => dashboardView === 'ar' ? isAR(tx) : isAP(tx));

  const projectFilteredTxs = dashboardView === 'all'
    ? projectTxs
    : projectTxs.filter(tx => dashboardView === 'ar' ? isAR(tx) : isAP(tx));

  // Category breakdown for organization-level
  const orgCatMap = {};
  orgFilteredTxs.forEach(tx => {
    const cat = tx.Category || 'Other';
    orgCatMap[cat] = (orgCatMap[cat] || 0) + Number(tx.Amount || tx.amount || 0);
  });
  const orgCategoryTotals = Object.entries(orgCatMap).map(([category, total]) => ({ category, total }));

  // Category breakdown for project-level (grouped by project)
  const projectsWithTxs = projects.map(project => {
    const txs = (project.transactions || []).filter(tx =>
      dashboardView === 'all'
        ? true
        : dashboardView === 'ar'
        ? isAR(tx)
        : isAP(tx)
    );
    const catMap = {};
    txs.forEach(tx => {
      const cat = tx.Category || 'Other';
      catMap[cat] = (catMap[cat] || 0) + Number(tx.Amount || tx.amount || 0);
    });
    const categoryTotals = Object.entries(catMap).map(([category, total]) => ({ category, total }));
    return {
      ...project,
      txs,
      categoryTotals,
    };
  });

  // Chart data for organization-level
  const orgDoughnutData = {
    labels: orgCategoryTotals.map(c => c.category),
    datasets: [{
      data: orgCategoryTotals.map(c => c.total),
      backgroundColor: [
        '#3b82f6', '#10b981', '#f97316', '#ef4444', '#8b5cf6', '#06b6d4', '#f59e0b'
      ].slice(0, orgCategoryTotals.length)
    }]
  };

  const getCardStyle = (status) => {
    // Example: style based on status string
    switch ((status || '').toLowerCase()) {
      case 'completed':
        return { border: '2px solid #10b981', background: '#f0fdf4' };
      case 'in progress':
        return { border: '2px solid #3b82f6', background: '#eff6ff' };
      case 'pending':
        return { border: '2px solid #f59e0b', background: '#fffbeb' };
      default:
        return { border: '2px solid #e5e7eb', background: '#fff' };
    }
  };

  // --- RENDER ---
  return (
    <div className="dashboard-main">
      <div className="dashboard-container">
        {/* Header */}
        <div className="dashboard-header">
          <div className="header-left">
            <button
              className="back-btn"
              onClick={typeof onBack === 'function' ? onBack : () => window.history.back()}
            >
              ‚Üê Back
            </button>
            <h1>
              {organization?.name || 'Organization'}
            </h1>
          </div>
          <div className="header-actions" style={{ display: 'flex', gap: 12 }}>
            <button
              onClick={() => typeof onAddProject === 'function' && onAddProject(true)}
              style={{
                border: 'none',
                background: '#2563eb',
                color: '#fff',
                padding: '8px 16px',
                borderRadius: 6,
                cursor: 'pointer',
                fontWeight: 500,
                fontSize: 16
              }}
            >
              + Add Project
            </button>
            <label htmlFor="company-upload-file">
              <input
                id="company-upload-file"
                type="file"
                accept=".csv,.xlsx"
                style={{ display: 'none' }}
                onChange={onFileUpload}
              />
              <button
                type="button"
                style={{
                  border: 'none',
                  background: '#2563eb',
                  color: '#fff',
                  padding: '8px 16px',
                  borderRadius: 6,
                  cursor: 'pointer',
                  fontWeight: 500,
                  fontSize: 16
                }}
                onClick={() => document.getElementById('company-upload-file').click()}
              >
                üìÅ Upload File
              </button>
            </label>
          </div>
        </div>

        {/* Summary Cards (tab selector) */}
        <div className="summary-grid">
          <SummaryCard
            title="Accounts Receivable"
            value={totals.ar}
            icon={TrendingUp}
            color={dashboardView === 'ar' ? "card-green selected" : "card-green"}
            change="+15%"
            changeType="positive"
            onClick={() => setDashboardView('ar')}
            style={getCardStyle('ar')}
          />
          <SummaryCard
            title="Accounts Payable"
            value={totals.ap}
            icon={TrendingDown}
            color={dashboardView === 'ap' ? "card-orange selected" : "card-orange"}
            change="-5%"
            changeType="negative"
            onClick={() => setDashboardView('ap')}
            style={getCardStyle('ap')}
          />
          <SummaryCard
            title="Profit / Loss"
            value={Math.abs(totals.ar - totals.ap)}
            icon={IndianRupee}
            color={totals.ar - totals.ap >= 0 ? "card-green" : "card-red"}
            change={totals.ar - totals.ap >= 0 ? "Profit" : "Loss"}
            changeType={totals.ar - totals.ap >= 0 ? "positive" : "negative"}
          />
          {dashboardView !== 'all' && (
            <button
              style={{
                marginLeft: 12,
                padding: '8px 18px',
                borderRadius: 8,
                border: 'none',
                background: '#e5e7eb',
                color: '#2563eb',
                fontWeight: 500,
                cursor: 'pointer'
              }}
              onClick={() => setDashboardView('all')}
            >
              Show All
            </button>
          )}
        </div>

        {/* Organization-level Overview */}
        <div style={{ margin: '18px 0' }}>
          <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 4px 16px rgba(0,0,0,0.04)', padding: 24 }}>
            <h2 style={{ marginBottom: 18 }}>
              Organization Level {dashboardView === 'ar' ? 'Income' : dashboardView === 'ap' ? 'Expenses' : 'Overview'}
            </h2>
            <div style={{ height: 320, width: '100%', display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
              <Doughnut data={orgDoughnutData} />
            </div>
            <div>
              <h4>Category Breakdown</h4>
              {orgCategoryTotals.length === 0 && <div style={{ color: '#666' }}>No records yet.</div>}
              {orgCategoryTotals.map(c => (
                <div key={c.category} style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #f0f0f0' }}>
                  <div style={{ fontWeight: 600 }}>{c.category}</div>
                  <div style={{ fontWeight: 700 }}>
                    {c.total.toLocaleString('en-IN', { style: 'currency', currency: 'INR' })}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Project-level Overview */}
        <div style={{ margin: '18px 0' }}>
          <div style={{ background: '#fff', borderRadius: 16, boxShadow: '0 4px 16px rgba(0,0,0,0.04)', padding: 24 }}>
            <h2 style={{ marginBottom: 18 }}>
              Project Level {dashboardView === 'ar' ? 'Income' : dashboardView === 'ap' ? 'Expenses' : 'Overview'}
            </h2>
            {projectsWithTxs.map(project => (
              <div key={project.id || project.ProjectID} style={{ marginBottom: 28, padding: 16, borderRadius: 12, background: '#f9fafb', boxShadow: '0 2px 8px rgba(0,0,0,0.03)' }}>
                <h4 style={{ marginBottom: 8 }}>{project.name || project.ProjectName} <span style={{ color: '#888', fontSize: 13 }}>({project.id || project.ProjectID})</span></h4>
                <div style={{ marginBottom: 8 }}>
                  <strong>Total:</strong> {project.txs.reduce((sum, tx) => sum + Number(tx.Amount || tx.amount || 0), 0).toLocaleString('en-IN', { style: 'currency', currency: 'INR' })}
                </div>
                <div style={{ marginBottom: 8 }}>
                  <strong>Category Breakdown:</strong>
                  {project.categoryTotals.length === 0 && <span style={{ color: '#666', marginLeft: 8 }}>No records</span>}
                  {project.categoryTotals.map(c => (
                    <div key={c.category} style={{ display: 'flex', justifyContent: 'space-between', padding: '4px 0', fontSize: 14 }}>
                      <span>{c.category}</span>
                      <span>{c.total.toLocaleString('en-IN', { style: 'currency', currency: 'INR' })}</span>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Department Filter */}
        <select
          value={selectedDept}
          onChange={(e) => onDeptChange(e.target.value)}
          className="dept-filter"
          style={{
            marginLeft: 6,
            padding: '6px 10px',
            borderRadius: 6,
            border: '1px solid #e5e7eb',
            fontSize: 14
          }}
        >
          <option value="all">All Departments</option>
          <option value="infra">Infra</option>
          <option value="designstudioz">DesignStudioz</option>
        </select>

        {/* Projects Section */}
        <div className="projects-section">
          <div className="section-header">
            <h2>
              {dashboardView === 'ar'
                ? 'Accounts Receivable Projects'
                : dashboardView === 'ap'
                ? 'Accounts Payable Projects'
                : selectedDept === 'all'
                ? 'All Projects'
                : selectedDept === 'infra'
                ? 'Infrastructure Projects'
                : selectedDept === 'designstudioz'
                ? 'Design Studio Projects'
                : 'Projects'}
            </h2>
            <div className="project-count">
              {visibleProjects.length} active projects
            </div>
          </div>
          <div className="projects-grid">
            {visibleProjects.map((project) => (
              <ProjectCard
                key={project.id}
                project={project}
                onSelect={onProjectSelect}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CompanyDashboard;
