import React, { useState, useEffect } from 'react';
import { Building2, Code } from 'lucide-react';
import { useNavigate, useLocation } from 'react-router-dom';
import OrganizationSelector from './OrganizationSelector';
import CompanyDashboard from './CompanyDashboard';
import ProjectDashboard from './ProjectDashboard';
import Header from './Header';
import '../Styles/Dashboard.css';
import * as XLSX from 'xlsx';
import Papa from 'papaparse';

const API_BASE = 'http://localhost:3001';
const dbFields = [
  'OrganizationID', 'OrgName', 'TxnID', 'TxnDate', 'Category', 'Item', 'Type', 'Amount', 'ProjectID', 'ProjectName'
];

const OrganizationDashboard = ({ isLoggedIn, onLogout }) => {
  const location = useLocation();
  const userEmail = location.state?.userEmail || '';
  const organizationId = location.state?.organizationId || null;

  // --- State declarations ---
  const [view, setView] = useState(() => localStorage.getItem('dashboardView') || 'organizations');
  const [selectedOrg, setSelectedOrg] = useState(() => {
    const org = localStorage.getItem('selectedOrg');
    return org ? JSON.parse(org) : null;
  });
  const [selectedDept, setSelectedDept] = useState(() => localStorage.getItem('selectedDept') || 'all');
  const [selectedProject, setSelectedProject] = useState(() => {
    const proj = localStorage.getItem('selectedProject');
    return proj ? JSON.parse(proj) : null;
  });
  const [organizations, setOrganizations] = useState([]);
  const [rawProjects, setRawProjects] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [totals, setTotals] = useState({ budget:0, spent:0, profit:0, ar:0, ap:0, team:0 });
  const [showNewProjectForm, setShowNewProjectForm] = useState(false);

  // --- Excel/DB Import states ---
  const [excelData, setExcelData] = useState([]);
  const [excelColumns, setExcelColumns] = useState([]);
  const [columnMap, setColumnMap] = useState({});
  const [uploadFileName, setUploadFileName] = useState('');
  const [importedRows, setImportedRows] = useState([]); // Use this for calculations, not for UI
  const [projectsFromDB, setProjectsFromDB] = useState([]); // Use this for calculations, not for UI
  const [step, setStep] = useState(1); // 1: upload, 2: map

  // --- Load organizations ---
  useEffect(() => {
    const loadOrgs = async () => {
      try {
        const res = await fetch(`${API_BASE}/organizations`);
        const data = await res.json();
        const mapped = data.map(o => ({
          id: o.OrganizationID,
          name: o.Name,
          type: o.Type || '',
          description: o.Description || '',
          icon: o.Name && o.Name.toLowerCase().includes('collabridge') ? Code : Building2,
          color: o.Name && o.Name.toLowerCase().includes('la tierra') ? '#10b981' : '#3b82f6'
        }));
        setOrganizations(mapped);
      } catch (err) {
        console.error('Error loading organizations:', err);
      }
    };
    loadOrgs();
  }, []);

  // --- Organization selection ---
  const handleOrgSelect = async (orgId) => {
    setSelectedDept('all');
    setSelectedProject(null);
    setView('dashboard');
    setStep(1); // Reset import step

    try {
      const orgRes = await fetch(`${API_BASE}/organizations/${orgId}`);
      const orgData = await orgRes.json();

      const projRes = await fetch(`${API_BASE}/projects?organizationId=${orgId}`);
      const projRows = await projRes.json();

      const deptRes = await fetch(`${API_BASE}/departments?organizationId=${orgId}`);
      const deptRows = await deptRes.json();

      const mappedProjects = (projRows || []).map(p => ({
        id: p.ProjectID || p.ProjectId || p.id,
        name: p.ProjectName || p.Name || '',
        status: (p.Status || '').toString(),
        budget: Number(p.Budget || 0),
        spent: Number(p.Spending || 0),
        profit: Number(p.Profit !== undefined ? p.Profit : (Number(p.Budget || 0) - Number(p.Spending || 0))),
        ar: Number(p.AR || 0),
        ap: Number(p.AP !== undefined ? p.AP : Number(p.Spending || 0)),
        team: Number(p.Team || 0),
        deadline: p.EndDate || p.deadline || '',
        departmentName: p.DepartmentName || ''
      }));

      setRawProjects(mappedProjects);
      setSelectedOrg({
        id: orgData.OrganizationID || orgId,
        name: orgData.Name && orgData.Name.trim() ? orgData.Name : (organizations.find(o => o.id === orgId)?.name || ''),
        type: orgData.Type && orgData.Type.trim() ? orgData.Type : (organizations.find(o => o.id === orgId)?.type || ''),
        description: orgData.Description && orgData.Description.trim() ? orgData.Description : (organizations.find(o => o.id === orgId)?.description || ''),
        projects: mappedProjects,
        color: organizations.find(o => o.id === orgId)?.color || '#3b82f6',
        icon: organizations.find(o => o.id === orgId)?.icon || Building2
      });

      setDepartments((deptRows || []).map(d => ({ id: d.DepartmentID, name: d.Name })));
    } catch (err) {
      console.error('Error selecting organization:', err);
    }
  };

  // --- Excel Upload & Import ---
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setUploadFileName(file.name);

    if (file.name.endsWith('.csv')) {
      Papa.parse(file, {
        header: true,
        skipEmptyLines: true,
        complete: (results) => {
          setExcelData(results.data);
          setExcelColumns(Object.keys(results.data[0] || {}));
          setStep(2);
        },
        error: () => setStep(1)
      });
      return;
    }

    const reader = new FileReader();
    reader.onload = (evt) => {
      const data = evt.target.result;
      const workbook = XLSX.read(data, { type: 'binary' });
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const json = XLSX.utils.sheet_to_json(sheet, { defval: '' });
      setExcelData(json);
      setExcelColumns(Object.keys(json[0] || {}));
      setStep(2);
    };
    reader.readAsBinaryString(file);
  };

  // --- Save column mapping and import to DB ---
  const handleMappingSave = async (mapping) => {
    setColumnMap(mapping);
    const mappedRows = excelData.map(row => {
      const mappedRow = {};
      Object.entries(mapping).forEach(([dbField, excelCol]) => {
        mappedRow[dbField] = row[excelCol] || '';
      });
      return mappedRow;
    });

    await fetch(`${API_BASE}/import-transactions`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        rows: mappedRows,
        uploaderEmail: userEmail,
        fileName: uploadFileName,
        organizationId: selectedOrg?.id || null,
        columnMap: mapping
      })
    });

    // Reload imported transactions and project financials from DB
    const importedRes = await fetch(`${API_BASE}/imported-transactions`);
    const imported = await importedRes.json();
    setImportedRows(imported);

    const projFinRes = await fetch(`${API_BASE}/project-financials`);
    const projFin = await projFinRes.json();
    setProjectsFromDB(Array.isArray(projFin) ? projFin : []);

    setStep(1); // Reset step, or keep as needed
    // Now use importedRows and projectsFromDB in your existing dashboard calculations
  };

  // --- UI ---
  return (
    <>
      <Header isLoggedIn={isLoggedIn} onLogout={onLogout} />
      {view === 'organizations' && (
        <OrganizationSelector
          organizations={organizations}
          onSelect={handleOrgSelect}
        />
      )}

      {view === 'project' && selectedProject && (
        <ProjectDashboard
          project={selectedProject}
          organization={selectedOrg}
          onBack={() => setView('dashboard')}
        />
      )}

      {view === 'dashboard' && (
        <div
          style={{
            width: '100vw',
            height: '100vh',
            padding: '32px 48px',
            background: '#f5f7fa',
            borderRadius: 0,
            boxShadow: 'none',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start',
            justifyContent: 'flex-start',
            overflow: 'auto'
          }}
        >
          <h2 style={{ marginBottom: 24 }}>Accounts Dashboard</h2>
          {/* --- Excel Import Steps --- */}
          {step === 1 && (
            <div style={{ marginBottom: 32 }}>
              <p>Upload Excel/CSV to import transactions:</p>
              <input type="file" accept=".xlsx,.xls,.csv" onChange={handleFileUpload} />
            </div>
          )}
          {step === 2 && (
            <MappingModal
              columns={excelColumns}
              onMap={handleMappingSave}
              onCancel={() => setStep(1)}
            />
          )}

          {/* --- Cards Row --- */}
          <div
            style={{
              display: 'flex',
              flexDirection: 'row',
              gap: '32px',
              width: '100%',
              marginBottom: 40
            }}
          >
            <DashboardCard title="Accounts Receivable" value={totals.ar} color="#4caf50" />
            <DashboardCard title="Accounts Payable" value={totals.ap} color="#f44336" />
            <DashboardCard title="Profit" value={totals.profit} color="#2196f3" />
            <DashboardCard title="Loss" value={totals.loss} color="#ff9800" />
          </div>

          {/* --- Existing CompanyDashboard --- */}
          <CompanyDashboard
            organization={selectedOrg}
            selectedDept={selectedDept}
            onDeptChange={setSelectedDept}
            onProjectSelect={setSelectedProject}
            onAddProject={setShowNewProjectForm}
            onFileUpload={handleFileUpload}
            onBack={() => setView('organizations')}
            projects={rawProjects}
            totals={totals}
          />
        </div>
      )}
    </>
  );
};

// --- Mapping Modal Component ---
const MappingModal = ({ columns, onMap, onCancel }) => {
  const [map, setMap] = useState({});
  return (
    <div style={{ marginTop: 40 }}>
      <h3>Map Excel Columns to DB Fields</h3>
      {dbFields.map(field => (
        <div key={field} style={{ marginBottom: 8 }}>
          <label>{field}: </label>
          <select value={map[field] || ''} onChange={e => setMap(m => ({ ...m, [field]: e.target.value }))}>
            <option value="">-- Select --</option>
            {columns.map(col => <option key={col} value={col}>{col}</option>)}
          </select>
        </div>
      ))}
      <button onClick={() => onMap(map)} style={{ marginRight: 12 }}>Save Mapping & Import</button>
      <button onClick={onCancel}>Cancel</button>
    </div>
  );
};

// Add this DashboardCard component if not already present:
const DashboardCard = ({ title, value, color }) => (
  <div
    style={{
      flex: '1 1 0',
      background: color,
      color: '#fff',
      borderRadius: 12,
      padding: '32px 24px',
      textAlign: 'center',
      boxShadow: '0 2px 8px #0002',
      minWidth: 200,
      fontSize: 20
    }}
  >
    <div style={{ fontSize: 18, marginBottom: 8 }}>{title}</div>
    <div style={{ fontSize: 32, fontWeight: 700 }}>
      ₹{Number(value).toLocaleString()}
    </div>
  </div>
);

export default OrganizationDashboard;
