import React from 'react';
import { Doughnut } from 'react-chartjs-2';
import { useLocation } from 'react-router-dom';

const ARAPBreakdown = () => {
  const location = useLocation();
  const { projects, organization, type } = location.state || {};

  if (!projects || !organization || !type) {
    return <div>Invalid data</div>;
  }

  const filteredProjects = projects.filter(p => type === 'ar' ? p.ar > 0 : p.ap > 0);

  return (
    <div style={{ padding: 32 }}>
      <button onClick={() => window.history.back()} style={{ marginBottom: 18 }}>← Back</button>
      <h2>
        {type === 'ar' ? 'Accounts Receivable' : 'Accounts Payable'} — Project Breakdown
      </h2>
      <div style={{ height: 320, width: '100%', display: 'flex', justifyContent: 'center', marginBottom: 24 }}>
        <Doughnut
          data={{
            labels: filteredProjects.map(p => p.name || p.projectName || `Project ${p.id}`),
            datasets: [{
              data: filteredProjects.map(p => type === 'ar' ? p.ar : p.ap),
              backgroundColor: [
                '#3b82f6', '#10b981', '#f97316', '#ef4444', '#8b5cf6', '#06b6d4', '#f59e0b'
              ].slice(0, filteredProjects.length)
            }]
          }}
        />
      </div>
      <div>
        <h4>Project Breakdown</h4>
        {filteredProjects.length === 0 && <div style={{ color: '#666' }}>No records yet.</div>}
        {filteredProjects.map(p => (
          <div key={p.id} style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderBottom: '1px solid #f0f0f0' }}>
            <div style={{ fontWeight: 600 }}>{p.name || p.projectName || `Project ${p.id}`}</div>
            <div style={{ fontWeight: 700 }}>
              {(type === 'ar' ? p.ar : p.ap).toLocaleString('en-IN', { style: 'currency', currency: 'INR' })}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ARAPBreakdown;
